import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [inputData, setInputData] = useState({
    u10: 2.0,
    v10: -2.0,
    u100: 3.0,
    v100: -3.0,
    hour: 12,
    day: 15,
    month: 6,
    year: 2023,
  });
  const [prediction, setPrediction] = useState(null);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setInputData({
      ...inputData,
      [name]: parseFloat(value),
    });
  };

  const handlePredict = async () => {
    try {
      const response = await axios.post('http://127.0.0.1:5000/predict', inputData);
      setPrediction(response.data.prediction);
    } catch (error) {
      console.error('Error making prediction:', error);
    }
  };

  return (
    <div className="App">
  <div class="left-section">
    <img src="https://cdn.dribbble.com/users/1186624/screenshots/4322005/windmill-illustration-3.gif" alt="Animated Background" id='img'></img>
  </div>
      <h1>Wind Power Production Forecasting</h1>
      <div className="input-form">
        <label>
          Zonal Wind Velocity at 10m (u10, m/s):
          <input
            type="number"
            name="u10"
            value={inputData.u10}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Meridional Wind Velocity at 10m (v10, m/s):
          <input
            type="number"
            name="v10"
            value={inputData.v10}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Zonal Wind Velocity at 100m (u100, m/s):
          <input
            type="number"
            name="u100"
            value={inputData.u100}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Meridional Wind Velocity at 100m (v100, m/s):
          <input
            type="number"
            name="v100"
            value={inputData.v100}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Hour (0-23):
          <input
            type="number"
            name="hour"
            value={inputData.hour}
            onChange={handleInputChange}
            min="0"
            max="23"
          />
        </label>
        <label>
          Day (1-31):
          <input
            type="number"
            name="day"
            value={inputData.day}
            onChange={handleInputChange}
            min="1"
            max="31"
          />
        </label>
        <label>
          Month (1-12):
          <input
            type="number"
            name="month"
            value={inputData.month}
            onChange={handleInputChange}
            min="1"
            max="12"
          />
        </label>
        <label>
          Year (2020-2024):
          <input
            type="number"
            name="year"
            value={inputData.year}
            onChange={handleInputChange}
            min="2020"
            max="2024"
          />
        </label>
        <button onClick={handlePredict}>Predict</button>
      </div>
      {prediction !== null && (
        <div className="prediction-result">
          <h2>Predicted Normalized Wind Power Production: {prediction.toFixed(2)}</h2>
          <p>(Normalized by the maximum output of the wind farm)</p>
        </div>
      )}
    </div>
  );
}

export default App;
